import { compare } from "./compare";
/**
 * HOF comparator. Returns new comparator to sort objects by given `key`
 * and with optional comparator `cmp` (default: {@link compare}).
 *
 * @param key -
 * @param cmp -
 */
export const compareByKey = (key, cmp = compare) => (x, y) => cmp(x[key], y[key]);
/**
 * HOF comparator. Returns new comparator to sort objects by given keys
 * `a` (major), `b` (minor) and with optional comparators (default for
 * each: {@link compare}).
 *
 * @param a -
 * @param b -
 * @param cmpA -
 * @param cmpB -
 */
export const compareByKeys2 = (a, b, cmpA = compare, cmpB = compare) => (x, y) => {
    let res = cmpA(x[a], y[a]);
    return res === 0 ? cmpB(x[b], y[b]) : res;
};
/**
 * Same as {@link compareByKeys2}, but for 3 sort keys / comparators.
 *
 * @param a -
 * @param b -
 * @param c -
 * @param cmpA -
 * @param cmpB -
 * @param cmpC -
 */
export const compareByKeys3 = (a, b, c, cmpA = compare, cmpB = compare, cmpC = compare) => (x, y) => {
    let res = cmpA(x[a], y[a]);
    return res === 0
        ? (res = cmpB(x[b], y[b])) === 0
            ? cmpC(x[c], y[c])
            : res
        : res;
};
/**
 * Same as {@link compareByKeys2}, but for 4 sort keys / comparators.
 *
 * @param a -
 * @param b -
 * @param c -
 * @param d -
 * @param cmpA -
 * @param cmpB -
 * @param cmpC -
 * @param cmpD -
 */
export const compareByKeys4 = (a, b, c, d, cmpA = compare, cmpB = compare, cmpC = compare, cmpD = compare) => (x, y) => {
    let res = cmpA(x[a], y[a]);
    return res === 0
        ? (res = cmpB(x[b], y[b])) === 0
            ? (res = cmpC(x[c], y[c])) === 0
                ? cmpD(x[d], y[d])
                : res
            : res
        : res;
};
